import React from 'react';

export default function Rate( score) {
	const stars = [1, 2, 3, 4, 5];
	return (
		<div className="rate-container">
            {stars.forEach(element => {
                if(element<=score){
                    <li key={element} className='Star-item red'>★</li>
                } else{
                    <li key={element} className='Star-item grey'>★</li>
                }
                
            })}
			{stars.map((level) =>
				score <= level ? (
					<li className='Star-item red'>★</li>
				) : ( //affichage conditionnel des étoiles de score, pleine ou vide selon le score passé en props
                <li className='Star-item grey'>★</li>
				)
			)}
		</div>
	);
}
