import React, { Component }  from 'react';
import { useParams } from 'react-router';
import JsonData from '../../data/logements.json'
import Slider from '../../components/Slider/Slider.jsx'
import {IoIosArrowDown, IoIosArrowDropleft, IoIosArrowDropright} from "react-icons/io";


function Product() {
 /* Récupère la bonne fiche */
 const id = useParams();
 const Product = JsonData.find(logement => logement.id === id.id)
  

  console.log(Product);
  return (
    <div>
      <h1>{Product.title}</h1>
      <div className='slider-container'>
        <div className='left-arrow'><IoIosArrowDropleft/></div>
        <div className='right-arrow'><IoIosArrowDropright/></div>
        <Slider slides={Product.pictures}/>
      </div>
    </div>
  )
}

export default Product
