import React from 'react';
import {useState} from 'react';


function Collapsible({title,content}) {
    const initialText = title;
    const [open, setOPen] = useState(false);
    const [ButtonText, setButtonText] = useState(initialText);
    const toggle = () => {
        setOPen(!open);
        setButtonText(title + <p className="collapse__arrow">&lt;</p>)
    };
    return (
        <div className='about__dropdowns__items'>
        <button className='about__dropdowns__items--title' onClick={toggle}>{ButtonText}</button>
        {open && (
        <div className="about__dropdowns__items--description">
            <h4>{content}</h4>
        </div>
        )}
        </div>
    );
}
export default Collapsible;
